import React, { useState,useEffect } from 'react';
import './HomePage.scss';


import{
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useParams,
    Redirect
  } from 'react-router-dom';



function Botones(props){

    let arrayBotones=[];
    for(let i=0;i<props.valoresBotones.length;i++){
        arrayBotones.push(
        <div>
            <div className="botones">
                <button className={props.valoresBotones[i].class}  onClick={()=>{props.enviar(i)}}><img src={props.valoresBotones[i].img}/>{props.valoresBotones[i].value}</button>
            </div>
            <div className="texto">
                {props.mensajes[i]}
            </div>

        </div>)
    }


    return(

            <div className="botones_y_texto">
                {arrayBotones}

            </div>

    );
}
export default Botones;
