import React, { useState,useEffect } from 'react';

import{
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useParams,
    Redirect
  } from 'react-router-dom';
  import App from "../App";


function Onboarding(){

    const[onboarding, setOnboarding]=useState({
                                img:"./assets/img/onboarding/pag1/scan2.png",
                                texto:"¡Bienvenido a Applergic! .Escanea el código de barras de tu producto y Applergic te dirá si es apto para ti.",
                                puntitos:"./assets/img/onboarding/pag1/mago51.png",
                                contador: 0,
                                valorBoton:"Siguiente",
                                clickFinal: ()=>cambiar()});

    const cambiar=()=>{
        onboarding.contador=onboarding.contador+1;
        let nuevosValores;
        if(onboarding.contador===1){
            nuevosValores={
                img:"./assets/img/onboarding/pag2/rectangle.png",
                texto:"Lleva tu Diario de compras y actividades.",
                puntitos:"./assets/img/onboarding/pag2/mago52.png",
                contador:1,
                valorBoton:"Siguiente",
                clickFinal: ()=>cambiar()
            };  
        } 
        else if(onboarding.contador===2){

            nuevosValores={
                img:"./assets/img/onboarding/pag3/ambulancia.png",
                texto:"En caso de emergencia nos pondremos en contacto con la persona que nos digas.",
                puntitos:"./assets/img/onboarding/pag3/mago53.png",
                contador:2,
                valorBoton:"Siguiente",
                clickFinal: ()=>cambiar()
            };  

        }
        else if(onboarding.contador===3){

            nuevosValores={
                img:"./assets/img/onboarding/pag4/traduccioN.png",
                texto:"Viaja a donde quieras. Tendrás a tu disposición un traductor off-line y tu informe de alergias e intolerancias traducido al idioma local",
                puntitos:"./assets/img/onboarding/pag4/mago54.png",
                contador:3,
                valorBoton:"Finalizar",
                clickFinal:location.href='/Login'


            };  

        }
        else if(onboarding.contador>=4){

            return; 

        }    
        setOnboarding(nuevosValores);

    }
    return(
        <div>
            <img src={onboarding.img}/>
            {onboarding.texto}
            <img src={onboarding.puntitos}/>
            <Router><Link to="/Login" >Finalizar</Link></Router>

            <a href='/Login'>Clickeame</a>
            <p onClick= {onboarding.clickFinal}>{onboarding.valorBoton}</p> 
        </div>
    );
}
export default Onboarding;
