import React, { useState,useEffect } from 'react';


function Onboarding(){

    const[onboarding, setOnboarding]=useState({
                                img:"./assets/img/onboarding/scan2.png",
                                texto:"¡Bienvenido a Applergic! .Escanea el código de barras de tu producto y Applergic te dirá si es apto para ti.",
                                puntitos:"./assets/img/onboarding/mago51.png",
                                contador: 0});

    const cambiar=()=>{
        onboarding.contador=onboarding.contador+1;
        console.log(onboarding.contador);
    }






    return(
        <div>
            <img src={onboarding.img}/>
            {onboarding.texto}
            <button onClick={cambiar()}>Siguiente</button>
        </div>
    );
}
export default Onboarding;
