import React, { useState,useEffect } from 'react';
import Botones from './Botones';
import './HomePage.scss';


import{
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useParams,
    Redirect
  } from 'react-router-dom';

function HomePage(){

    const valoresBotones=[{img:"./assets/img/Home/barcode2.png",value:"Escanear",class:"boton_escanear"},
                        {img:"./assets/img/Home/buscar.png",value:"Buscar",class:"boton_buscar"},
                        {img:"./assets/img/Home/sos1.png",value:"S.O.S",class:"boton_SOS"}];
    const mensajes=["Escanea un nuevo producto.","Busca un comercio o restaurante para ti.","¿Necesita ayuda urgente? contactamos con emergencias."];
    const menuHamburguesa=<nav>
                            <input type="checkbox" id="check"/>
                            <label for="check" className="checkbtn">
                                <img src="./assets/img/Home/menAzul.png"/>
                            </label>
                            <ul>
                            <li><Link><img src="./assets/img/Home/hamburguesa/close.png"/></Link></li>
                            <li><Link><img src="./assets/img/Home/hamburguesa/botNMen.png"/></Link></li>

                            <li><Link><img src="./assets/img/Home/hamburguesa/botNFav.png"/></Link></li>
                            <li><Link><img src="./assets/img/Home/hamburguesa/botNDiario.png"/></Link></li>
                            <li><Link><img src="./assets/img/Home/hamburguesa/botNCompartir.png"/></Link></li>
                            <li><Link><img src="./assets/img/Home/hamburguesa/botNTrad.png"/></Link></li>
                            <li><Link><img src="./assets/img/Home/hamburguesa/botNTerminos.png"/></Link></li>
                            <li><Link><img src="./assets/img/Home/hamburguesa/botNMenAzul.png"/></Link></li>
                            </ul>
                           </nav>

    const FuncionBotones=(i)=>{
        if(i===0){
            alert("Has pulsado el boton escanear");
            window.location.href='/login';   //......PONER LA RUTA CORRECTA EN ESTOS TRES
        }
        if(i===1){
            alert("Has pulsado el boton buscar");
            window.location.href='/login';
        }
        if(i===2){
            alert("Has pulsado el boton sos");
            window.location.href='/login';
        };
    }

    return(

            <div className="home_page">

                <div className="cabecera">
                    <div>{menuHamburguesa}</div>
                    <div><figure><img src="./assets/img/Home/gris.png"/></figure></div>

                </div>
                <div className="logo"><figure><img src="./assets/img/Home/logoApplergicFinal.png"/></figure></div>

                <Botones valoresBotones={valoresBotones} mensajes={mensajes} enviar={FuncionBotones}></Botones>
                <div className="botones_inferiores">
                    <img src="./assets/img/Home/homeAzul.png"/>
                    <img src="./assets/img/Home/favorito.png"/>
                    <img src="./assets/img/Home/diario.png"/>
                    <img src="./assets/img/Home/red.png"/>
                </div>

            </div>

    );
}
export default HomePage;
