import React, { useState,useEffect } from 'react';
import Botones from './Botones';
import './HomePage.scss';


import{
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useParams,
    Redirect
  } from 'react-router-dom';

function HomePage(){

    const valoresBotones=[{img:"./assets/img/Home/barcode2.png",value:"Escanear",class:"botonEscanear"},
                        {img:"./assets/img/Home/buscar.png",value:"Buscar",class:"botonBuscar"},
                        {img:"./assets/img/Home/sos1.png",value:"S.O.S",class:"botonSOS"}];
    const mensajes=["Escanea un nuevo producto.","Busca un comercio o restaurante para ti.","¿Necesita ayuda urgente? contactamos con emergencias."];

    const FuncionBotones=(i)=>{
        if(i===0){
            alert("Has pulsado el boton escanear");
            window.location.href='/login';   //......PONER LA RUTA CORRECTA EN ESTOS TRES
        }
        if(i===1){
            alert("Has pulsado el boton buscar");
            window.location.href='/login';
        }
        if(i===2){
            alert("Has pulsado el boton sos");
            window.location.href='/login';
        };
    }

    return(

            <div>
                <div className="cabecera">
                    <figure><img src="./assets/img/Home/menAzul.png"/></figure>
                    <figure><img src="./assets/img/Home/gris.png"/></figure>
                </div>
                <figure className="logo"><img src="./assets/img/Home/logoApplergicFinal.png"/></figure>
                <Botones valoresBotones={valoresBotones} mensajes={mensajes} enviar={FuncionBotones}></Botones>
                <div>
                    <figure><img src="./assets/img/Home/homeAzul.png"/></figure>
                    <figure><img src="./assets/img/Home/favorito.png"/></figure>
                    <figure><img src="./assets/img/Home/diario.png"/></figure>
                    <figure><img src="./assets/img/Home/red.png"/></figure>
                </div>

            </div>

    );
}
export default HomePage;
