import React, { useState,useEffect } from 'react';


function Login(){
    const validar=()=>{
        console.log('estoy en validar')
        let email=document.getElementById("emailLogin").value;
        let password=document.getElementById("passwordLogin").value;

    }

    window.onload=function(){
        document.forms.Login.onsubmit=validar;
        document.getElementById("emailLogin").onblur=validar;
        document.getElementById("passwordLogin").onblur=validar;
    }



    return(
        <div>
            <figure><img src="./assets/img/login/image.png"/></figure>
            <p>¡Bienvenido de nuevo!</p>
            <p>Por favor, introduce tus datos para continuar</p>
            <form>
                <input type="email" id="emailLogin" placeholder="Dirección e.mail"/>
                <input type="password" id="passwordLogin" placeholder="Password"/>
                <input type="submit" value="Entrar"/>

            </form>
            <p>¿Nuevo en Applergic?</p>
            <p>Crea tu cuenta aquí</p>
            <p>Me registraré en otro momento</p>
        </div>
    );
}
export default Login;
